			, M_EMAIL
			, M_PHONE
			, ZIPCODE
			, ADDRESS
			, ADDRESS2
			, M_JOB
			, M_ROLE)
VALUES(#{mem_id}
			, #{m_passwd}
			, #{m_name}
			, #{m_email}
			, #{m_phone}
			, #{zipcode}
			, #{address}
			, #{address2}
			, #{m_job}
			, 'mem')

mapper.update:
UPDATE MEMBER
SET M_PASSWD = #{m_passwd}
WHERE MEM_ID = #{mem_id}
