mapper.update:
UPDATE ORDERT
SET STATE =#{state} 
WHERE O_NO = #{o_no}
AND   P_NO = #{p_no}
AND   MEM_ID=#{mem_id}

mapper.select:
    
     SELECT * FROM ORDERT
     WHERE MEM_ID =#{mem_id}
	 AND STATE < 5

mapper.delete:
DELETE FROM ORDERT
WHERE MEM_ID = #{mem_id}
 AND STATE =5

update.foreach:
		UPDATE ORDERT SET
        STATE = #{item.state},
        O_REGDATE = SYSDATE
		WHERE O_NO=#{item.o_no}
		AND P_NO = #{item.p_no}
		AND MEM_ID = #{item.mem_id}
