mapper.select:
SELECT P_NO, STOCK, PRICE, P_NAME, DETAIL, PR_DATE, IMAGE, PATH, STATE
FROM PRODUCT
WHERE P_NO=#{p_no}

mapper.update:
UPDATE PRODUCT SET STOCK = #{stock}, PRICE = #{price}, P_NAME = #{p_name}, DETAIL = #{detail}, STATE = #{state}, PR_DATE = sysdate,
IMAGE = #{image},
PATH = #{path} WHERE P_NO = #{p_no}
