mapper.select:
SELECT P_NO, STOCK, PRICE, P_NAME, DETAIL, PR_DATE, IMAGE, PATH, STATE, WRITER
FROM PRODUCT
WHERE P_NO=#{p_no}

mapper.update:
UPDATE PRODUCT SET STOCK = #{stock}, PRICE = #{price}, P_NAME = #{p_name}, DETAIL = #{detail}, STATE = #{state}, WRITER = #{writer}, PR_DATE = sysdate,
IMAGE = #{image},
PATH = #{path} WHERE P_NO = #{p_no}

mapper.select:
SELECT * FROM (SELECT ROWNUM AS RR, P_NO, STOCK, PRICE, P_NAME, DETAIL, PR_DATE, IMAGE, PATH, STATE, WRITER FROM (SELECT P_NO, STOCK, PRICE, P_NAME, DETAIL, PR_DATE, IMAGE, PATH, STATE, WRITER FROM PRODUCT
WHERE STATE = #{state}
ORDER BY P_NO) A)
ORDER BY PRICE) A)
ORDER BY PRICE DESC) A)
WHERE RR BETWEEN #{start} and #{end}
